import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import style from './Header.module.css';
import SearchIcon from '@material-ui/icons/Search';
import AddIcon from '@material-ui/icons/Add';
import logo from '../../Assests/logo.svg';
import { Link } from 'react-router-dom';
import { Search } from './Search';
import { OutSideClick } from './OutsideClick';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}
export function Header() {
  const classes = useStyles();

  const [display, setdisplay] = useState(false);
  const [open, setOpen] = React.useState(false);
  const [modalStyle] = React.useState(getModalStyle);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const loginHandler = () => {
    console.log('Open Modal');
  };

  const body = (
    <div style={modalStyle} className={classes.paper}>
      <h2 id='simple-modal-title'>Text in a modal</h2>
      <p id='simple-modal-description'>
        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
      {/* <SimpleModal /> */}
    </div>
  );

  return (
    <div className={style.HeaderContainer}>
      <Grid container justify='center' spacing={1} alignItems='center'>
        <Grid
          container
          justify='center'
          // spacing={1}
          item
          xs={6}
          sm={4}
          alignItems='center'
        >
          <Grid item xs={12} sm={3}>
            <div className={style.Heading}>
              <Link to='/'>
                <img src={logo} alt='logo' height='50px' />
              </Link>
            </div>
          </Grid>
          <Grid item xs={12} sm={9}>
            <OutSideClick setDisplay={setdisplay}>
              <Search display={display} setdisplay={setdisplay} />
            </OutSideClick>
          </Grid>
        </Grid>

        <Grid
          container
          item
          xs={6}
          sm={8}
          justify='center'
          spacing={1}
          alignItems='center'
          className={style.secongrid}
        >
          <Grid item xs={12} sm={9}>
            <div className={style.Search2}>
              <input
                type='text'
                placeholder='Find Cars, Mobile Phones and more...'
              />
              <SearchIcon className={style.searchicon2} />
            </div>
          </Grid>
          <Grid item xs={6} sm={1}>
            <div className={style.LoginHeading} onClick={handleOpen}>
              <p className={style.LoginHeadingwrite}> Login </p>
              <p className={style.LoginHeadingunderline}></p>
            </div>
          </Grid>
          <Grid item xs={6} sm={2}>
            <div className={style.LoginSell}>
              <div>
                <AddIcon className={style.LoginSellicon} />
                <p>Sell</p>
              </div>
            </div>
          </Grid>
        </Grid>
      </Grid>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby='simple-modal-title'
        aria-describedby='simple-modal-description'
      >
        <p>Heeeeeee</p>
      </Modal>
    </div>
  );
}
